import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import ProductsMock from 'src/mock-data/product.mock';
import { Product } from 'src/models/product.model';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css'],
})
export class ProductDetailsComponent {
  productDetails: Product | undefined;

  products: Product[] = ProductsMock;

  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    this.route.queryParams.subscribe(this.findProduct.bind(this));
  }

  findProduct(queryParams: any): void {
    this.productDetails = this.products.find(
      (item) => item.id == queryParams.productId
    );
  }
}
<div class="container">
  <div class="row">
    <div class="col">
      <h1>Product-details</h1>

      <div
        *ngIf="productDetails; else noDetails"
        class="alert alert-success"
        role="alert"
      >
        Hey!! We found the prodcut. And it's a {{ productDetails.name }}
      </div>

      <ng-template #noDetails>
        <div class="alert alert-danger" role="alert">
          Sorry! The product you are looking is not a valid one.
        </div>
      </ng-template>
    </div>
  </div>
</div>
