import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-user-details',
  templateUrl: './user-details.component.html',
  styleUrls: ['./user-details.component.css'],
})
export class UserDetailsComponent {
  userDetails: any;
  isLoading = true;

  constructor(private route: ActivatedRoute, private http: HttpClient) {}

  ngOnInit() {
    this.route.queryParams.subscribe(this.getUserDetails.bind(this));
  }

  getUserDetails(query: any) {
    const { userId } = query;
    this.http
      .get(`https://jsonplaceholder.typicode.com/users/${userId}`)
      .subscribe(this.handleResponse.bind(this));
  }

  handleResponse(response: any) {
    this.userDetails = response;
    this.isLoading = false;
  }
}
<div class="container">
  <div class="row">
    <div class="col-6 mx-auto">
      <h1 class="my-4">Users Details</h1>

      <div class="spinner-border" role="status">
        <span class="visually-hidden">Loading...</span>
      </div>

      <table class="table table-bordered">
        <tbody>
          <tr>
            <th scope="row">Full Name</th>
            <td>{{ userDetails.name }}</td>
          </tr>
          <tr>
            <th scope="row">Username</th>
            <td>{{ userDetails.username }}</td>
          </tr>
          <tr>
            <th scope="row">Phone Number</th>
            <td>{{ userDetails.phone }}</td>
          </tr>
          <tr>
            <th scope="row">Email</th>
            <td>{{ userDetails.email }}</td>
          </tr>
          <tr>
            <th scope="row">Website</th>
            <td>{{ userDetails.website }}</td>
          </tr>
          <tr>
            <th scope="row">Address</th>
            <td>{{ userDetails.address.city }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
